cmp.config:
buttonAlign:'center',
						region:'center'
						,padding: '5px' 
						, items: [{
								xtype: 'textfield',
								name: 'URL',
								fieldLabel: 'Ingrese una URL valida',
								anchor:'100%'

							}]

						,buttons: [
							{text:'Aceptar',handler:function(){ 

							const URL = this.findParentByType('panel').find('textarea[name="URL"')[0].getValue();

							command.call(
								this,
								{ URL: URL }); 
								panel.hide();}
							} 
							,{text:'Cancelar',handler:function(){ 
								panel.hide();}}
							]
