panel.config:
region: 'center', width:200,collapseMode:'mini',split:true,minWidth:50, listeners:{ render: { fn: function(){

				this.dv.on('dblclick', function() { 

					this.obj.get_inspect_panel() && this.obj.inspect_window();
				});
			}
		}}

show_buttons: false, region:'center',labelWidth:70, 
		defaults:{width: '150px'},
		listeners: { render: { fn: function(){

			this.imagen_cambia_imagen = function() {

				var img, r;

				if ( ! ( img = Ext.get('xbox_imagen_xpForm') ) ) return;

				if ( r = this.cr() ) {

                			img.dom.src 	= App.feat.uri_thumb + r.data.rotar + '&src=' + r.data.dirname + '/' + r.data.basename;
					img.dom.style 	= "border:2px solid black; max-width: 100%; max-height: 100%";
					/* img.dom.border 	= 0; */
					img.dom.alt 	= r.data.imagen_ID_label;

				} else img.dom.src = '/ext/resources/images/default/s.gif';
			};

			this.store.on('changerowindex', this.imagen_cambia_imagen );
			this.store.on('load', this.imagen_cambia_imagen );
			this.on('afterlayout', this.imagen_cambia_imagen, this.store );

		}}
		}

cmp.config:
xtype: 'box',margins: '5 5 5 5', style: { width: '100px' }, autoScroll: true,
								autoEl: {
								id: 'xbox_imagen_xpForm', tag: 'img', src: '/ext/resources/images/default/s.gif'
								}
