                function incrementa( $nombre ) {/*{{{*/

                        if (!$nombre) {

                                M()->error( 'debe especificar el nombre del contador' );
                                return NULL;

                        }

                        if ( !$this->load( array( 'nombre' => $nombre ) ) ) {

				$this->fill_primary_key();
                                $this->nombre = $nombre;
                                $this->numero = 1;
                                $this->insert();

                        } else {

                                $this->numero ++;
                                $this->update();
                        }

                        return $this->numero;
                }/*}}}*/
